package lu.uni.lcsb.vizbin.graphics;

/**
 * Defines shapes of points for visualization.
 * 
 * @author Piotr Gawron
 * 
 */
public enum PointShape {
	/**
	 * Circle.
	 */
	CIRCLE,
	/**
	 * Cross.
	 */
	CROSS,
	/**
	 * Triangle that face up.
	 */
	TRIANGLE_UP,
	/**
	 * Triangle that face down.
	 */
	TRIANGLE_DOWN,
	/**
	 * Plus.
	 */
	PLUS,
	/**
	 * Rectangle.
	 */
	RECTANGLE,
	/**
	 * Star.
	 */
	STAR;
}
